package org.example;import org.example.controller.AuthController;import org.example.controller.ManageController;import java.util.Scanner;public class Manager {    private static AuthController authController = null;    private static ManageController manageController = null;    public void start() {        authController = new AuthController();        manageController = new ManageController();        run();    }    private static void run() {        Scanner scanner = new Scanner(System.in);        System.out.println("1 - login   2 - register");        switch (scanner.nextInt()) {            case 1 -> login();            case 2 -> register();        }        run();    }    private static void register() {        Scanner scanner = new Scanner(System.in);        System.out.print("Name >> ");        String name = scanner.next();        System.out.print("Surname >> ");        String surname = scanner.next();        System.out.print("Phone >> ");        String phone = scanner.next();        System.out.print("Password >> ");        String password = scanner.next();        authController.register(name, surname, phone, password);    }    private static void login() {        Scanner scanner = new Scanner(System.in);        System.out.print("Phone >> ");        String phone = scanner.next();        System.out.print("Password >> ");        String password = scanner.next();        try {            authController.login(phone, password);        } catch (Exception e) {            System.out.println(e.getMessage());        }    }}