package org.example.controller;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.Setter;import org.example.entity.Profile;import org.example.service.AdminService;import org.example.util.Utils;@Getter@Setter@AllArgsConstructorpublic class AdminController {    private  CardController cardController;    private  TerminalController terminalController;    private  ProfileController profileController;    public AdminController() {        cardController = new CardController();        terminalController = new TerminalController();        profileController = new ProfileController();    }    public AdminController(CardController cardController,TerminalController terminalController) {    }    public void manage(Profile profile) {        System.out.println("1 - Cards   2 - Terminals   3 - Profiles   4 - Transactions   5 - Statistics   0 - Exit");        Integer menu = Utils.getIntScanner();        switch (menu) {            case 1 -> {                cardController.manage(profile);            }            case 2 -> {                terminalController.manage(profile);            }            case 3 -> {                profileController.manage(profile);            }            case 4 -> {                cardController.transactionList();            }            case 5 -> {                cardController.statistics(profile);            }            case 0 -> {                return;            }            default -> {                System.out.println("Try again!");            }        }        manage(profile);    }}