package org.example.controller;import lombok.Getter;import lombok.Setter;import org.example.service.AuthService;import org.example.service.impl.AuthServiceImpl;@Getter@Setterpublic class AuthController {    private final AuthService authService;    public AuthController() {        this.authService = new AuthServiceImpl();    }    public void login(String phone, String password) {        authService.login(phone, password);    }    public void register(String name, String surname, String phone, String password) {        authService.register(name, surname, phone, password);    }}