package org.example.controller;import org.example.entity.Profile;public class ManageController {   private final AdminController adminController;   private final UserController userController;    public ManageController() {        adminController = new AdminController();        userController = new UserController();    }    public void manage(Profile profile) {        if (profile.getRole().equals(Profile.Role.ADMIN)) {            System.out.println("ADMIN-MENU");            adminController.manage(profile);        } else {            System.out.println("USER-MENU");            userController.manage(profile);        }    }}