package org.example.controller;import org.example.entity.Profile;import org.example.service.TerminalService;import org.example.service.impl.TerminalServiceImpl;import org.example.util.Utils;public class TerminalController {    private final TerminalService terminalService;    public TerminalController() {        terminalService = new TerminalServiceImpl();    }    public void manage(Profile profile) {        System.out.println("1 - create Terminal   2 - TerminalList   3 - Update(code,address)   4 - Change Status   5 - Delete Terminal   0 - Exit");        Integer menu = Utils.getIntScanner();        switch (menu) {            case 1 -> {                createTerminal();            }            case 2 -> {                getTerminalList();            }            case 3 -> {                updateTerminal();            }            case 4 -> {                changeTerminalStatus();            }            case 5 -> {                deleteTerminal();            }            case 0 -> {                return;            }            default -> {                System.out.println("Try again!");            }        }        manage(profile);    }    private void createTerminal() {            terminalService.createTerminal();    }    private void getTerminalList() {        terminalService.getTerminalList();    }    private void updateTerminal() {        terminalService.updateTerminal();    }    private void changeTerminalStatus() {        terminalService.changeTerminalStatus();    }    private void deleteTerminal() {        terminalService.deleteTerminal();    }}