package org.example.controller;import org.example.entity.Profile;import org.example.service.CardService;import org.example.service.impl.CardServiceImpl;import org.example.util.Utils;import java.math.BigDecimal;public class UserController {    private final CardService cardService;    public UserController() {        cardService = new CardServiceImpl();    }    public void manage(Profile profile) {        System.out.println("1 - Card Settings   2 - Transaction");        int menu  = Utils.getIntScanner();        switch (menu) {            case 1 -> cardSettings(profile);            case 2 -> transaction(profile);            default -> System.out.println("Try again!");        }        manage(profile);    }    private void transaction(Profile profile) {        System.out.println("1 - ATTO Pay   2 - Transaction List");        int menu = Utils.getIntScanner();        switch (menu) {            case 1 -> {                System.out.println("Input Terminal number >> ");                String number = Utils.getStringScanner();                cardService.attoPay(profile, number);            }        }    }    public void cardSettings(Profile profile) {        System.out.println("1 - Add Card   2 - Card List   3 - Card Change Status   4 -  Refill Card   5 - Delete Card   0 - Exit");        int menu = Utils.getIntScanner();        switch (menu) {            case 1 -> cardService.addCard(profile.getId(), profile.getPhone());            case 2 -> cardService.cardList(profile.getPhone());            case 3 -> cardService.changeCardStatus(profile.getPhone());            case 4 -> {                System.out.print("Input amount >> ");                BigDecimal amount;                amount = Utils.getBigDecimalScanner();                cardService.refill(profile.getPhone(), amount);            }            case 5 -> cardService.deleteCard(profile.getPhone());            case 0 -> {                return;            }            default -> System.out.println("Try again!");        }        cardSettings(profile);    }}