package org.example.entity;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import lombok.ToString;import java.time.LocalDateTime;import java.util.Locale;@Getter@Setter@NoArgsConstructor@ToStringpublic class Profile {    private Integer id;    private String name;    private String surname;    private String phone;    private String password;    private LocalDateTime createdDate;    private Status status;    private Role role;    public Profile(String name, String surname, String phone, String password) {        this.name = name;        this.surname = surname;        this.phone = phone;        this.password = password;    }    public enum Status {        ACTIVE, NON_ACTIVE    }    public enum Role {        ADMIN, USER    }}