package org.example.service.impl;import lombok.Getter;import lombok.Setter;import org.example.controller.ManageController;import org.example.entity.Profile;import org.example.repository.ProfileRepository;import org.example.service.AuthService;import org.example.util.Utils;@Getter@Setterpublic class AuthServiceImpl implements AuthService {    private final ProfileRepository repository;    private final ManageController manageController;    public AuthServiceImpl() {        this.repository = new ProfileRepository();        this.manageController = new ManageController();    }    @Override    public void login(String phone, String password) {        Profile profile = repository.getByPhone(phone).orElseThrow();        if (profile.getPassword().trim().equals(password)) {            manageController.manage(profile);        } else {            System.out.println("Failed");        }    }    @Override    public void register(String name, String surname, String phone, String password) {        try {            Profile profile = new Profile(name, surname, phone, password);            profile.setRole(Profile.Role.USER);            profile.setStatus(Profile.Status.ACTIVE);            profile.setCreatedDate(Utils.getNow());            repository.register(profile);            System.out.println("Success");        } catch (Exception e) {            System.out.println("Failed");        }    }}