package org.example.service.impl;import org.example.entity.Profile;import org.example.entity.Terminal;import org.example.repository.TerminalRepository;import org.example.service.TerminalService;import org.example.util.DataBaseManager;import org.example.util.Utils;import java.sql.Connection;import java.sql.PreparedStatement;import java.sql.ResultSet;import java.sql.SQLException;import java.time.LocalDateTime;import static org.example.util.Utils.formatter;public class TerminalServiceImpl implements TerminalService {    private final TerminalRepository terminalRepository;    public TerminalServiceImpl() {        terminalRepository = new TerminalRepository();    }    @Override    public void createTerminal() {        terminalRepository.createTerminal();    }    @Override    public void getTerminalList() {        terminalRepository.getTerminalList();    }    @Override    public void updateTerminal() {        terminalRepository.updateTerminal();    }    @Override    public void changeTerminalStatus() {        terminalRepository.changeTerminalStatus();    }    @Override    public void deleteTerminal() {        terminalRepository.deleteTerminal();    }}