package org.example.util;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public interface DataBaseManager {    String dataSourcePath = "jdbc:postgresql://localhost:5432/";    String dataSourceName = "db-atto";    String user = "postgres";    String password = "postgres";    static Connection getConnection() throws ClassNotFoundException, SQLException {        Class.forName("org.postgresql.Driver");        return DriverManager.getConnection(dataSourcePath + dataSourceName, user, password);    }}