package org.example.util;import java.math.BigDecimal;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;import java.util.Random;import java.util.Scanner;public interface Utils {    DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss");    Random random = new Random();    Scanner scanner = new Scanner(System.in);    static LocalDateTime getNow() {        String dt = LocalDateTime.now().format(formatter);        return LocalDateTime.parse(dt, formatter);    }    static String getNowString() {        return getNow().format(formatter);    }    static String dateToString(LocalDateTime localDateTime) {        return localDateTime.format(formatter);    }    static LocalDateTime stringToDate(String localDateTime) {        return LocalDateTime.parse(localDateTime, formatter);    }    static String getCardNumber() {        return "9860" + "-" +                random.nextInt(999, 10_000) +                "-" +                random.nextInt(999, 10_000) +                "-" +                random.nextInt(999, 10_000);    }    static String getExpDate() {        LocalDateTime localDateTime = Utils.getNow();        return localDateTime.getMonth().name()                + "/" + localDateTime.getYear();    }    static String getStringScanner() {        return scanner.next();    }    static Integer getIntScanner() {        return scanner.nextInt();    }    static BigDecimal getBigDecimalScanner() {        return scanner.nextBigDecimal();    }}