drop table profiles;create table profiles(    id           serial primary key,    name         varchar(100) not null,    surname      varchar(100) not null,    phone        varchar(13)  not null unique,    password     varchar(100) not null,    created_date timestamp default now(),    status       varchar(25)  not null,    role         varchar(25)  not null);drop table cards;create table cards(    id           serial primary key,    number       varchar(16) not null unique ,    exp_date     varchar     not null,    balance      decimal     not null,    status       varchar     not null,    phone        varchar(13),    created_date timestamp   not null,    foreign key (id) references profiles(id));select * from cards;